mapper.select:
select T.TagID, T.TagName, T.Remarks from CMS_CONTENT_TAG CT join
CMS_TAG T on CT.TagID = T.TagID and
CT.IsDelete = 0 and T.IsDelete = 0 and ContentID = #{contentID,jdbcType=INTEGER};

select
,
from CMS_CONTENT_NEWS N join CMS_CONTENT C on C.IsDelete = 0 and N.ContentID = C.ContentID and C.ContentID = #{contentID,jdbcType=INTEGER}

		select

		,

		from CMS_CONTENT_NEWS N
		join CMS_CONTENT C on C.IsDelete = 0 and N.ContentID = C.ContentID
		join CMS_CHANNEL L on L.IsDelete = 0 and C.ChannelID =
		L.ChannelID
		and L.ChannelPath = #{channelPath,jdbcType=VARCHAR}
		and C.ContentType = '2'
		and CASE
        WHEN
            (C.StartShowDate <> ''
                AND C.EndShowDate <> '')
        THEN
        	C.StartShowDate <= #{cmsContent.startShowDate,jdbcType=VARCHAR}
                AND C.StartShowTime <= #{cmsContent.startShowTime,jdbcType=VARCHAR}
                AND C.EndShowDate >= #{cmsContent.endShowDate,jdbcType=VARCHAR}
                AND C.EndShowTime >= #{cmsContent.endShowTime,jdbcType=VARCHAR}
        ELSE
		C.StartShowTime <= #{cmsContent.startShowTime,jdbcType=VARCHAR}
		and C.EndShowTime >= #{cmsContent.endShowTime,jdbcType=VARCHAR}
		END

			and C.CreateUserID = #{cmsContent.createUserID,jdbcType=INTEGER}

		order by C.StickyIndex desc, C.${page.sortField} ${page.sortType} limit ${page.start}, ${page.pageSize}

mapper.update:
update CMS_CONTENT_NEWS
set Source = #{source,jdbcType=VARCHAR},
SourceURL =
#{sourceURL,jdbcType=VARCHAR}
where ContentID = #{contentID,jdbcType=INTEGER}
