mapper.update:
update CHANNEL_CONTENT
set ChannelID = #{channelContent.channelID,jdbcType=INTEGER},
ContentID = #{channelContent.contentID,jdbcType=INTEGER}
where 
ContentID = #{channelContent.contentID,jdbcType=INTEGER} and
ChannelID = #{oldChannelID,jdbcType=INTEGER}

mapper.delete:
delete from CHANNEL_CONTENT
where ContentID = #{contentID,jdbcType=INTEGER} and
ChannelID = #{channelID,jdbcType=INTEGER}
