mapper.select:
SELECT
, S.PV FROM IDO_CMS_CONTENT C inner join IDO_CMS_CHANNEL L on C.ChannelID = L.ChannelID and L.ChannelPath = #{channelPath,jdbcType=VARCHAR} and L.IsDelete = 0 and C.IsDelete = 0 inner join (SELECT ContentID FROM IDO_CMS_CONTENT_TAG T where T.ClassCode = #{tagClass,jdbcType=VARCHAR} and T.ItemCode = #{tagItem,jdbcType=VARCHAR} and T.IsDelete = 0) T on C.ContentID = T.ContentID left outer join (SELECT count(*) PV, TableDataID FROM IDO_CMS_STATISTICS S where s.TableName = "IDO_CMS_CONTENT" and s.StatisticsType = 1 and S.IsDelete = 0) S on S.TableDataID = C.ContentID order by S.PV ${sortType}
LIMIT 0, ${count}

SELECT

, T.ItemCode FROM IDO_CMS_CONTENT C
inner join IDO_CMS_CHANNEL L
on C.ChannelID = L.ChannelID and L.ChannelPath = #{channelPath,jdbcType=VARCHAR} and
L.IsDelete = 0 and C.IsDelete = 0
inner join
(SELECT ContentID, ItemCode FROM IDO_CMS_CONTENT_TAG T
where T.ClassCode = #{tagClass,jdbcType=VARCHAR}
and

	T.ItemCode = #{tagItem,jdbcType=VARCHAR}


	T.ItemCode in
	#{item}

and T.IsDelete = 0) T
on C.ContentID = T.ContentID order by T.ItemCode ${sortType}

	LIMIT 0, ${count}

SELECT

FROM IDO_CMS_CONTENT C
INNER JOIN IDO_CMS_CHANNEL L
ON C.ChannelID = L.ChannelID AND L.ChannelPath = #{channelPath,jdbcType=VARCHAR}
AND L.IsDelete = 0
AND C.IsDelete = 0

	INNER JOIN
	(SELECT ContentID, ItemCode FROM IDO_CMS_CONTENT_TAG T
	WHERE T.ClassCode = #{tagClass,jdbcType=VARCHAR} AND

		T.ItemCode = #{tagItem,jdbcType=VARCHAR}


		T.ItemCode IN
		#{item}

	AND T.IsDelete = 0) T ON C.ContentID = T.ContentID

ORDER BY ${sortBy} ${sortType}

	LIMIT 0, ${count}
