mapper.select:
		select count(C.ContentID)		
		from IDO_CMS_CONTENT C
		join IDO_CMS_CONTENT_PRODUCT P on C.IsDelete = 0 and C.ContentID = P.ContentID 

        and P.ProductType = #{product.productType,jdbcType=INTEGER}


        and P.ProductCountry = #{product.productCountry,jdbcType=INTEGER}


        and P.ProductOrigin = #{product.productOrigin,jdbcType=INTEGER}

		and C.ChannelID in
		#{item.channelID}

		select

		from IDO_CMS_CONTENT C
		join IDO_CMS_CONTENT_PRODUCT P on C.IsDelete = 0 and C.ContentID = P.ContentID 

        and P.ProductType = #{product.productType,jdbcType=INTEGER}


        and P.ProductCountry = #{product.productCountry,jdbcType=INTEGER}


        and P.ProductOrigin = #{product.productOrigin,jdbcType=INTEGER}

		and C.ChannelID in
		#{item.channelID}
		order by C.ChannelID asc, C.ContentID asc, C.UpdateTime desc limit #{page.start}, #{page.end}

select
,
from IDO_CMS_CONTENT C join IDO_CMS_CONTENT_PRODUCT P on C.IsDelete = 0 and C.ContentID = P.ContentID and C.ContentID = #{contentID}

mapper.update:
update IDO_CMS_CONTENT_PRODUCT
set ProductType = #{productType,jdbcType=CHAR},
  ProductCountry = #{productCountry,jdbcType=CHAR},
  ProductOrigin = #{productOrigin,jdbcType=CHAR}
where ContentID = #{contentID,jdbcType=INTEGER}
